var app=angular.module("testAngular",["ngRoute"]);app.config(["$routeProvider",function(t){t.when("/",{templateUrl:"pages/MyComponent.html",controller:"MyComponentCtrl"}).when("/page1",{templateUrl:"pages/Page1.html",controller:"Page1Ctrl"}).when("/page2",{templateUrl:"pages/Page2.html",controller:"Page2Ctrl"}).otherwise({redirectTo:"/"})}]),app.controller("MyComponentCtrl",["$location",function(t){var o=this;o.goPage1=function(){t.path("/page1")},o.goPage2=function(){t.path("/page2")}}]),app.controller("Page1Ctrl",["$http","$location",function(t,o){var e=this;e.json="json",e.getjson=function(){t.get("http://localhost:3000/messages").success(function(t){e.json=t})},e.goHome=function(){o.path("/")}}]),app.controller("Page2Ctrl",["$http","$location",function(t,o){var e=this;e.json="name",e.getjson=function(){t.get("http://localhost:3000/messages").success(function(t){e.json=t.name})},e.goHome=function(){o.path("/")}}]);